var cadena;
var cod;
var tiempo;
var elemento = RegExp("(TIENE|TIENES|TENGO|TENES|TENÉS)");
var saludo = RegExp("(HOLA)");
var conversacion = RegExp("(BIEN Y TU?|BIEN Y VOS?|BIEN Y USTED?)");
var edad =/AÑOS/;
var contando = 0;

function evaluarEdad(){
	cadena = document.getElementById('txtPregunta').value;
	cadena = cadena.toUpperCase();

	document.getElementById("txtPregunta").innerHTML += "<br>" + cadena; 
	document.getElementById("contenedor-agregar").innerHTML += "<div>" + cadena;
	document.getElementById("txtPregunta").value = "";

	document.getElementById("contenedor-agregar").className= "globoCliente"; 
	
	
	if (elemento.test(cadena) && edad.test(cadena)) {
		cod = 0

	}

	if (saludo.test(cadena)) {
		cod = 1

	}

	if (conversacion.test(cadena)) {
		cod = 2

	}

	responder();
} 


function responder(){

	switch (cod){
		case 0:
		mensaje = "LA ARLENE TIENE 12 AÑOS";
		break;

		case 1:
		mensaje = "HOLA, ¿COMO ESTÁS?"
		break;
		
		case 2:
		mensaje = "BIEN, GRACIAS."
		break;	

	}


	tiempo = window.setTimeout(tiempoRespuesta, 1000);

	function tiempoRespuesta() {
	
		document.getElementById("contenedor-agregar").innerHTML += "<div>" + mensaje;


	}
}
