var cadena;
var cod;
var tiempo;
var elemento = RegExp("(TIENE|TIENES|TENGO|TENES|TENÉS)");
var saludo = RegExp("(HOLA)");
var conversacion = RegExp("(BIEN Y TU?|BIEN Y VOS?|BIEN Y USTED?)");
var edad =/AÑOS/;

function evaluarEdad(){
	cadena = document.getElementById('txtPregunta').value;
	cadena = cadena.toUpperCase();

	document.getElementById("txtPregunta").innerHTML = cadena;
	document.getElementById("contenedor-agregar").innerHTML = cadena;
	document.getElementById("txtPregunta").value = "";

	document.getElementById("contenedor-agregar").style.color = "#000";
	document.getElementById("contenedor-agregar").style.backgroundColor='#FFC0C0';
	document.getElementById("contenedor-agregar").style.padding='0px';
	document.getElementById("contenedor-agregar").style.margin='3px';

	document.getElementById("contenedor-envia").style.color = "#000";
	document.getElementById("contenedor-envia").style.backgroundColor='#F0F0F0';
	document.getElementById("contenedor-envia").style.padding='0px';
	document.getElementById("contenedor-envia").style.margin='3px';


	if (elemento.test(cadena) && edad.test(cadena)) {
		cod = 0

	}

	if (saludo.test(cadena)) {
		cod = 1

	}

	if (conversacion.test(cadena)) {
		cod = 2

	}

	responder();
} 


function responder(){

	switch (cod){
		case 0:
		mensaje = "ROBOT: 60 AÑOS";
		break;

		case 1:
		mensaje = "ROBOT: HOLA, ¿COMO ESTÁS?"
		break;
		
		case 2:
		mensaje = "ROBOT: BIEN, GRACIAS."
		break;	

	}


	tiempo = window.setTimeout(tiempoRespuesta, 1000);

	function tiempoRespuesta() {
		document.getElementById("contenedor-envia").innerHTML= mensaje;
	}
}



