var cadena, cod;
var elemento = RegExp("(TIENE|TIENES|TENGO|TENES|TENÉS)");
var saludo = RegExp("(HOLA)");
var conversacion = RegExp("(BIEN Y TU?|BIEN Y VOS?|BIEN Y USTED?)");
var edad =/AÑOS/;

function evaluarEdad(){
	cadena = document.getElementById('txtPregunta').value;
	cadena = cadena.toUpperCase();

	document.getElementById("txtPregunta").innerHTML = cadena;
	document.getElementById("contenedor-agregar").innerHTML = cadena;
	document.getElementById("txtPregunta").value = "";


	if (elemento.test(cadena) && edad.test(cadena)) {
		cod = 0

	}

	if (saludo.test(cadena)) {
		cod = 1

	}

	if (conversacion.test(cadena)) {
		cod = 2

	}

	responder();
} 


function responder(){

	switch (cod){
		case 0:
		mensaje = "ROBOT: 60 AÑOS";
		break;

		case 1:
		mensaje = "ROBOT: HOLA, ¿COMO ESTÁS?"
		break;
		
		case 2:
		mensaje = "ROBOT: BIEN, GRACIAS."
		break;	
	}

	document.getElementById("contenedor-envia").innerHTML= mensaje;
}